import { Plugin } from '@form-validation/core';

type ExcludedCallback = (field: string, element: HTMLElement, elements: HTMLElement[]) => boolean;

export interface ExcludedOptions {
    excluded: ExcludedCallback;
}

export class Excluded extends Plugin<ExcludedOptions> {
    static defaultIgnore(_field: string, element: HTMLElement, _elements: HTMLElement[]): boolean;

    constructor(opts?: ExcludedOptions);
}
