import { type ValidateFunctionInterface, type ValidateOptions, type ValidateResult } from '@form-validation/core';

export type CompareDifferentCallback = () => string;

export interface DifferentOptions extends ValidateOptions {
    compare: string | CompareDifferentCallback;
}

export function different(): ValidateFunctionInterface<DifferentOptions, ValidateResult>;
