import { type ValidateFunctionInterface, type ValidateOptions, type ValidateResult } from '@form-validation/core';

export type CompareIdenticalCallback = () => string;

export interface IdenticalOptions extends ValidateOptions {
    compare: string | CompareIdenticalCallback;
}

export function identical(): ValidateFunctionInterface<IdenticalOptions, ValidateResult>;
